import React, { useState, useEffect, useRef } from 'react';
import { useScroll, animated, useSpring } from 'react-spring'
import ShoeInputForm from './ShoeInputForm';
import styles from '../css/shoe-tracking.scss'

const ShoeTracker = () => {
  const X_LINES = 40

  const PAGE_COUNT = 5

  const INITIAL_WIDTH = 20

  useEffect(() => {
    const savedShoes = JSON.parse(localStorage.getItem('shoes')) || [];
    setShoes(savedShoes);
  }, []);

  const containerRef = useRef(null);
  const barContainerRef = useRef<HTMLDivElement>(null);

  const [textStyles, textApi] = useSpring(() => ({
    y: '100%',
  }))

  const { scrollYProgress } = useScroll({
    container: containerRef,
    onChange: ({ value: { scrollYProgress } }) => {
      if (scrollYProgress > 0.7) {
        textApi.start({ y: '0' })
      } else {
        textApi.start({ y: '100%' })
      }
    },
    default: {
      immediate: true,
    },
  }); 
  const [shoes, setShoes] = useState([]);

  const handleAddShoe = (newShoe) => {
    setShoes([...shoes, newShoe]);

    localStorage.setItem('shoes', JSON.stringify([...shoes, newShoe]));
  };


  return (
    <div ref={containerRef} className={styles.body}>
      <div className={styles.animated__layers}>
        <animated.div ref={barContainerRef} className={styles.bar__container}>
          {Array.from({ length: X_LINES }).map((_, i) => (
            <animated.div
              key={i}
              className={styles.bar}
              style={{
                width: scrollYProgress.to(scrollP => {
                  const percentilePosition = (i + 1) / X_LINES

                  return INITIAL_WIDTH / 4 + 40 * Math.cos(((percentilePosition - scrollP) * Math.PI) / 1.5) ** 32
                }),
              }}
            />
          ))}
        </animated.div>
        <animated.div className={styles.bar__container__inverted}>
          {Array.from({ length: X_LINES }).map((_, i) => (
            <animated.div
              key={i}
              className={styles.bar}
              style={{
                width: scrollYProgress.to(scrollP => {
                  const percentilePosition = 1 - (i + 1) / X_LINES

                  return INITIAL_WIDTH / 4 + 40 * Math.cos(((percentilePosition - scrollP) * Math.PI) / 1.5) ** 32
                }),
              }}
            />
          ))}
        </animated.div>
        <animated.div
          className={styles.dot}
          style={{
            clipPath: scrollYProgress.to(val => `circle(${val * 100}%)`),
          }}>
          <h1 className={styles.title}>
            <span>
              <animated.span style={textStyles}>Aha!</animated.span>
            </span>
            <span>
              <animated.span style={textStyles}>You found me!</animated.span>
            </span>
          </h1>
        </animated.div>
      </div>
      {new Array(PAGE_COUNT).fill(null).map((_, index) => (
        <div className={styles.full__page} key={index} />
      ))}

      <h2 class='new-shoes'>is it time for some new shoes?</h2>
      {shoes.length != 0 && <h2>current shoe:</h2>}
      <ul>
        {shoes.map((shoe, index) => (
          <li key={index}>
            {shoe.name} (distance tracker: {shoe.acquisitionDate})
          </li>
        ))}
      </ul>
      {shoes.length === 0 && <ShoeInputForm onAddShoe={handleAddShoe} />}
    </div>
  );
};

export default ShoeTracker;
