import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Stats from './components/Stats';
import './App.css';

const App = () => {
  const [yearlyMiles, setYearlyMiles] = useState({});
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear()); // Default to current year
  const clientID = 120096;
  const clientSecret = 'bc3ec467a7464ae5be9fc7a7f6cc69f126945851';
  const refreshToken = 'f545ac531dd1373ffca07f31d543f2e197810468'; 
  const auth_link = "https://www.strava.com/oauth/token";
  const activities_link = "https://www.strava.com/api/v3/athlete/activities";
  const perPage = 30; // Number of activities per page

  useEffect(() => {
    const fetchData = async (page = 1) => {
      try {
        const stravaAuthResponse = await axios.post(`${auth_link}?client_id=${clientID}&client_secret=${clientSecret}&refresh_token=${refreshToken}&grant_type=refresh_token`);
        const accessToken = stravaAuthResponse.data.access_token;

        const stravaActivityResponse = await axios.get(`${activities_link}?access_token=${accessToken}&page=${page}&per_page=${perPage}`);

        const activities = stravaActivityResponse.data; // Assuming the activities are an array in the response

        // Extract and organize activities by years
        const activitiesByYear = splitActivitiesByYear(activities);

        console.log(activitiesByYear);
        // Do something with the organized data (e.g., set it to state)
        // setOrganizedActivities(activitiesByYear);


        fetchData(page + 1);

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    const splitActivitiesByYear = (activities) => {
      const activitiesByYear = {};

      activities.forEach(activity => {
        const year = new Date(activity.date).getFullYear();

        if (!activitiesByYear[year]) {
          activitiesByYear[year] = [];
        }

        activitiesByYear[year].push(activity);
      });

      return activitiesByYear;
    };

    // Call the fetchData function to start fetching data
    fetchData();
  }, []); // Empty dependency array to run the effect only once on mount

  // Dummy runner stats data
  const runnerStats = {
    distance: 150, // Replace with actual distance
    time: 20, // Replace with actual time
    // Add more stats as needed
  };

  return (
    <div className="bg-gray-200 min-h-screen">
      <RunnerStats selectedYear={selectedYear} stats={runnerStats} />
    </div>
  );
}

export default App;
