// Auth.js
import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Auth = ({ setAccessToken }) => {
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // Function to extract the authorization code from the URL
    const extractCode = () => {
      const searchParams = new URLSearchParams(location.search);
      const code = searchParams.get('code');
      if (code) {
        // Call a function to exchange the code for an access token
        exchangeCodeForToken(code);
      } else {
        // Handle error or redirect user to the authorization URL
        navigate('/error');
      }
    };

    // Function to exchange the authorization code for an access token
    const exchangeCodeForToken = async (code) => {
      try {
        // Make a POST request to the token endpoint to exchange the code
        const response = await fetch('https://www.strava.com/api/v3/oauth/token', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            client_id: 'YOUR_CLIENT_ID',
            client_secret: 'YOUR_CLIENT_SECRET',
            code,
            grant_type: 'authorization_code',
          }),
        });

        // Parse the response and extract the access token
        const data = await response.json();
        const accessToken = data.access_token;

        // Update the access token in the parent component
        setAccessToken(accessToken);

        // Redirect the user to the home page or another route
        navigate('/');
      } catch (error) {
        console.error('Error exchanging code for token:', error);
        navigate('/error');
      }
    };

    // Call the extractCode function when the component mounts
    extractCode();
  }, [location.search, navigate, setAccessToken]);

  return <div>Authorizing...</div>;
};

export default Auth;
