// Stats.js
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Stats = ({ selectedYear, stats }) => {
  const [animationYear, setAnimationYear] = useState(2020);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setAnimationYear((prevYear) => (prevYear === 2024 ? 2020 : prevYear + 1));
    }, 1000); // Change the interval duration as needed

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-4xl font-bold mb-8">Stats</h1>

      <div className="flex items-center mb-4">
        <label className="mr-2">Select Year:</label>
        <select className="p-2 border rounded">
          {/* Populate options based on available years */}
          {[2020, 2021, 2022, 2023, 2024].map((year) => (
            <option key={year} value={year}>
              {year}
            </option>
          ))}
        </select>
      </div>

      <AnimatePresence>
        <motion.div
          key={animationYear}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="border p-4 rounded"
        >
          <h2 className="text-2xl font-bold mb-4">Stats for {animationYear}</h2>
          {/* Display runner stats here */}
          <p>Total Distance: miles</p>
          <p>Total Time: hours</p>
          {/* Add more stats as needed */}
        </motion.div>
      </AnimatePresence>
    </div>
  );
};

export default Stats;
