import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [accessToken, setAccessToken] = useState('');
  const [activities, setActivities] = useState([]);
  const clientID = 120096;
  const clientSecret = 'bc3ec467a7464ae5be9fc7a7f6cc69f126945851';
  const refreshToken = 'f545ac531dd1373ffca07f31d543f2e197810468'; 
  const auth_link = "https://www.strava.com/oauth/token"
  const activities_link = `https://www.strava.com/api/v3/athlete/activities`

  useEffect(() => {
    async function fetchData() {
      try {
        // Exchange refresh token for an access token
        const stravaAuthResponse = await axios.post(`${auth_link}?client_id=${clientID}&client_secret=${clientSecret}&refresh_token=${refreshToken}&grant_type=refresh_token`);

        // Fetch activities using the access token
        const stravaActivityResponse = await axios.get(`${activities_link}?access_token=${stravaAuthResponse.data.access_token}`);

        // Update state with the fetched activities
        setActivities(stravaActivityResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <h2>Activities</h2>
      <ul>
        {activities.map(activity => (
          <li key={activity.id}>{activity.name} - {activity.distance} meters</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
