// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Auth from './Auth'; // Assuming you have the Auth component

const App = () => {
  const [accessToken, setAccessToken] = useState('');
  const [runningData, setRunningData] = useState([]);

  // Function to fetch Strava data
  const fetchStravaData = async () => {
    try {
      const response = await axios.get('https://www.strava.com/api/v3/athlete/activities', {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });

      // Log the response data to the console
      console.log('Strava API Response:', response.data);

      // Update state with fetched running data
      setRunningData(response.data);
    } catch (error) {
      console.error('Error fetching Strava data:', error);
      // Handle errors or redirect to an error page
    }
  };

  // Use useEffect to fetch data when accessToken changes
  useEffect(() => {
    if (accessToken) {
      fetchStravaData();
    }
  }, [accessToken]);

  // Render your component UI
  return (
    <div className="App">
      <h1>on track</h1>
      <Auth setAccessToken={setAccessToken} /> {/* Assuming you have the Auth component */}
      {accessToken ? (
        <div>
          <p>Authenticated!</p>
          {/* Display running stats or other content */}
          <ul>
            {runningData.map((activity) => (
              <li key={activity.id}>
                <p>Date: {activity.start_date_local}</p>
                <p>Distance: {activity.distance} meters</p>
                {/* Add more details as needed */}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          <p>Not authenticated. Please login to fetch Strava data.</p>
        </div>
      )}
    </div>
  );
};

export default App;
