import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [accessToken, setAccessToken] = useState('');
  const [yearlyMiles, setYearlyMiles] = useState({});
  const clientID = 120096;
  const clientSecret = 'bc3ec467a7464ae5be9fc7a7f6cc69f126945851';
  const refreshToken = 'f545ac531dd1373ffca07f31d543f2e197810468'; 
  const auth_link = "https://www.strava.com/oauth/token";
  const activities_link = "https://www.strava.com/api/v3/athlete/activities";
  const perPage = 30; // Number of activities per page

  useEffect(() => {
    async function fetchData(page = 1) {
      try {
        const stravaAuthResponse = await axios.post(`${auth_link}?client_id=${clientID}&client_secret=${clientSecret}&refresh_token=${refreshToken}&grant_type=refresh_token`);

        // Fetch activities for the current page
        const stravaActivityResponse = await axios.get(`${activities_link}?access_token=${stravaAuthResponse.data.access_token}&page=${page}&per_page=${perPage}`);

        console.log('Headers:', stravaActivityResponse.headers);

        const yearlyMilesData = stravaActivityResponse.data.reduce((acc, activity) => {
          const activityDate = new Date(activity.start_date_local);
          const year = activityDate.getFullYear();
          const month = activityDate.getMonth(); 
          const distanceMeters = activity.distance;
          const distanceMiles = distanceMeters / 1609.34; 

          acc[year] = (acc[year] || 0) + distanceMiles;

          return acc;
        }, {});

        setYearlyMiles(prevData => ({ ...prevData, ...yearlyMilesData }));

        // If there are more pages, recursively fetch them
        if (stravaActivityResponse.headers['x-page'] < stravaActivityResponse.headers['x-total-pages']) {
          console.log("more pages"); 
          fetchData(page + 1);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <h2>Yearly Miles</h2>
      <ul>
        {Object.entries(yearlyMiles).map(([year, miles]) => (
          <li key={year}>{year}: {miles.toFixed(2)} miles</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
