import React, { useState, useEffect } from 'react';
import Stats from '../pages/Stats'
import axios from 'axios';
import Modal from 'react-modal'; 

const FetchDataFromStrava = () => {
  console.log("fetching"); 
  const [activities, setActivities] = useState([]);
  const [selectedYear] = useState(new Date().getFullYear()); 
  const [isAuthorizationPopupOpen, setAuthorizationPopupOpen] = useState(false);

  const openAuthorizationPopup = () => {
    setAuthorizationPopupOpen(true);
  };

  const closeAuthorizationPopup = () => {
    setAuthorizationPopupOpen(false);
  };

  const authorizeWithStrava = async () => {
    const response = await axios.post('http://localhost:4000/strava/auth');
    console.log("authorization"); 
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/strava/activities');
        const newActivities = response.data.activities;

        console.log("Strava API Response:", newActivities);

        setActivities(prevActivities => [...prevActivities, ...newActivities]);
      } catch (error) {
        console.error('Error fetching data:', error);
        console.log('popup here'); 
        <Modal isOpen={isAuthorizationPopupOpen} onRequestClose={closeAuthorizationPopup}>
          <h2>Authorization Required</h2>
          <p>Please authorize your Strava account to access the data.</p>
          <button onClick={authorizeWithStrava}>Authorize</button>
        </Modal>
      }
    };

    fetchData();
  }, []); 

  return (
      <div>
        <Stats year={selectedYear} activities={activities} />
      </div>
    );
};

export default FetchDataFromStrava;
