import React, { useState, useRef } from 'react';
import { motion, useScroll } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
// import StaggeredDropDown from './StaggeredDropDown.js';

const Stats = ({ activities }) => {
  const [selectedYear, setSelectedYear] = useState('all-time'); // Default to 'all-time'
  const isAllTime = selectedYear === 'all-time';

  const navigate = useNavigate();

  const handleClick = async () => {
    try {
      console.log("clicked");
      navigate('/');
      // navigate('/stats', { state: { selectedYear, stats: data } });
    } catch (error) {
      console.log(error);
    }
  };

  const handleDateChange = (event) => {
    const newDate = event.target.value;
    setSelectedYear(newDate);
  };


  return (
    <div className="min-h-screen" style={{ backgroundColor: 'rgb(255, 254, 247)', padding: '2.5rem' }}>
      <h1 className="text-4xl font-bold mb-8">
        {isAllTime ? (
          <motion.span
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, transition: { duration: 0.5 } }}
            transition={{ type: 'spring', stiffness: 200, damping: 20 }}
            className="inline-block"
          >
            {'all-time stats'}
          </motion.span>
        ) : (
          <>
            how you're doing in{' '}
            <motion.span
              key={selectedYear}
              initial={{ opacity: 0, y: -50 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, transition: { duration: 0.5 } }}
              transition={{ type: 'spring', stiffness: 200, damping: 20 }}
              className="inline-block"
            >
              {selectedYear}
            </motion.span>
          </>
        )}
      </h1>

      <div className="flex flex-wrap justify-center">
        <section className='stat-box'>
          <h2>total distance traveled</h2>
          <p>{activities.reduce((sum, activity) => sum + activity.distance, 0)} meters</p>
        </section>
        <section className='stat-box'>
          <h2>total distance traveled</h2>
          <p>{activities.reduce((sum, activity) => sum + activity.distance, 0)} meters</p>
        </section><section className='stat-box'>
          <h2>total distance traveled</h2>
          <p>{activities.reduce((sum, activity) => sum + activity.distance, 0)} meters</p>
        </section>
      </div>

      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={handleClick}
        className="bg-lightblue text-white font-wotfard text-l py-2 px-4 rounded mt-4"
      >
        back to home
      </motion.button>

      <label htmlFor="yearDropdown">Select Year:</label>
      <select id="yearDropdown" value={selectedYear} onChange={handleDateChange}>
        <option value="all-time">All Time</option>
        <option value="2022">2022</option>
        <option value="2021">2021</option>
        {/* Add more years as needed */}
      </select>
      </div>

  );
};

export default Stats;
