import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom'
import StaggeredDropDown from './StaggeredDropDown.js'

const Stats = ({ year, activities }) => {
  const isAllTime = year === 'all-time';

  const navigate = useNavigate();

  const handleClick = async () => {
    try {
      console.log("clicked"); 
      navigate('/'); 
      // navigate('/stats', { state: { selectedYear: '2023', stats: data } });
    } catch (error) {
      console.log(error); 
    }
  }

  return (
    <div className="min-h-screen" style={{ backgroundColor: 'rgb(255, 254, 247)', padding: '2.5rem' }}>
      <h1 className="text-4xl font-bold mb-8">
        {isAllTime
          ? (
          <motion.span
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, transition: { duration: 0.5 } }}
            transition={{ type: 'spring', stiffness: 200, damping: 20 }}
            className="inline-block"
          > {'all-time stats'}
          </motion.span>)
          : (
            <>
              how you're doing in{' '}
              <motion.span
                initial={{ opacity: 0, y: -50 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, transition: { duration: 0.5 } }}
                transition={{ type: 'spring', stiffness: 200, damping: 20 }}
                className="inline-block"
              >
                {year}
              </motion.span>
            </>
          )
        }
      </h1>

      <div>
        <div>
          <h2>total distance traveled</h2>
          <p>{activities.reduce((sum, activity) => sum + activity.distance, 0)} meters</p>
        </div>
      </div>
      <div className="flex justify-between items-end fixed bottom-0 w-full p-4 bg-white">
        <motion.button
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          onClick={handleClick}
          className="bg-lightblue text-white font-wotfard text-l py-2 px-4 rounded"
        >
          back to home
        </motion.button>

        <StaggeredDropDown />
      </div>
    </div>
  );
};

export default Stats;
