import React, { useEffect, useState } from 'react';
import './MoonMessage.css'; // Import your CSS file for styling

const MoonMessage = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleScroll = () => {
    const currentPosition = window.scrollY;
    setScrollPosition(currentPosition);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const calculateOCount = () => {
    // Adjust this factor based on how quickly you want the 'o' count to increase
    const factor = 0.1;
    return Math.floor(scrollPosition * factor);
  };

  const renderMoonMessage = () => {
    const oCount = calculateOCount();
    const os = 'o'.repeat(oCount);

    return (
      <div className="moon-message">
        <h1>To the M{os}n</h1>
      </div>
    );
  };

  return (
    <div>
      {/* Content above the moon message */}
      {renderMoonMessage()}
      {/* Content below the moon message */}
    </div>
  );
};

export default MoonMessage;
