import React, { useState } from 'react';
import axios from 'axios';

function ShoeInputForm() {
  const [brand, setBrand] = useState('');
  const [model, setModel] = useState('');
  const [acquisitionDate, setAcquisitionDate] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send form data to the /shoes endpoint
      await axios.post('/shoes', {
        brand,
        model,
        acquisition_date: acquisitionDate // Make sure to match the key with the backend
      });

      // Clear form fields after successful submission
      setBrand('');
      setModel('');
      setAcquisitionDate('');

      // Optionally, you can show a success message or redirect the user
      alert('Shoe added successfully');
    } catch (error) {
      console.error('Error adding shoe:', error);
      // Handle error (e.g., display error message to user)
    }
  };

  return (
    <form onSubmit={handleSubmit} className="w-full max-w-md mx-auto">
      <div className="mb-4">
        <label htmlFor="shoeName" className="block text-gray-700 font-bold mb-2">
          brand
        </label>
        <input
          type="text"
          id="brand"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
          className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          placeholder="enter brand name"
          required
        />
      </div>
      <div className="mb-4">
        <label htmlFor="shoeBrand" className="block text-gray-700 font-bold mb-2">
          brand
        </label>
        <input
          type="text"
          id="brand"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
          className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          placeholder="enter brand name"
          required
        />
      </div>
      <div className="mb-6">
        <label htmlFor="acquisitionDate" className="block text-gray-700 font-bold mb-2">
          acquisition date
        </label>
        <input
          type="date"
          id="acquisitionDate"
          value={acquisitionDate}
          onChange={(e) => setAcquisitionDate(e.target.value)}
          className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          required
        />
      </div>
      <div className="flex items-center justify-between">
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Add Shoe
        </button>
      </div>
    </form>
  );
};

export default ShoeInputForm;
