// App.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from './components/Header';
import Stats from './components/RunningStats';
import './styles/App.css';

const App = () => {
  const [accessToken, setAccessToken] = useState('');

  // Function to handle user login and redirect to Strava for authorization
  const handleLogin = () => {
    // Construct the Strava authorization URL
    const stravaAuthUrl = `https://www.strava.com/oauth/authorize?client_id=120096&redirect_uri=http://localhost:3000/auth&response_type=code&scope=read,activity:read_all`;

    // Redirect the user to Strava for authorization
    window.location.href = stravaAuthUrl;
  };

  return (
    <div className="App">
      <h1>Running Project</h1>
      {accessToken ? (
        <div>
          <p>Authenticated!</p>
          {/* Display running stats or other content */}
        </div>
      ) : (
        <div>
          <p>Not authenticated. Please login to fetch Strava data.</p>
          <button onClick={handleLogin}>Login with Strava</button>
        </div>
      )}
    </div>
  );
};

export default App;
