import React, { useState, useEffect, Fragment } from 'react';
import Stats from '../pages/Stats'
import axios from 'axios';
import { Dialog, Transition } from '@headlessui/react'; 

const FetchDataFromStrava = () => {
  console.log("fetching"); 
  const [activities, setActivities] = useState([]);
  const [selectedYear] = useState(new Date().getFullYear()); 
  let [isOpen, setIsOpen] = useState(false); 

  function closeModal() {
    setIsOpen(false)
  }

  function openModal() {
    setIsOpen(true)
  }

  const authorizeWithStrava = async () => {
    // const response = await axios.post('http://localhost:4000/strava/auth');
    closeModal(); 
    console.log("authorization"); 
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/strava/activities');
        const newActivities = response.data.activities;

        console.log("Strava API Response:", newActivities);

        setActivities(prevActivities => [...prevActivities, ...newActivities]);
      } catch (error) {
        console.error('*****Error fetching data:', error);
        openModal(); 
      }
    };

    fetchData();
  }, []); 

  return (
      <div>
        <div className="fixed inset-0 flex items-center justify-center">

      </div>

      <Transition appear show={isOpen} as={Fragment}>
        <Dialog as="div" className="relative z-10" onClose={closeModal}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black/25" />
          </Transition.Child>

          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex min-h-full items-center justify-center p-4 text-center">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="w-full max-w-md transform overflow-hidden rounded-2xl bg-white p-6 text-left align-middle shadow-xl transition-all">
                  <Dialog.Title
                    as="h3"
                    className="text-lg font-medium leading-6 text-gray-900"
                  >
                    uh oh!
                  </Dialog.Title>
                  <div className="mt-2">
                    <p className="text-sm text-gray-500">
                      you'll need to give us permission to use your stats from strava.
                    </p>
                  </div>

                  <div className="mt-4">
                    <button
                      type="button"
                      className="inline-flex justify-center rounded-md border border-transparent bg-blue-100 px-4 py-2 text-sm font-medium text-blue-900 hover:bg-blue-200 focus:outline-none focus-visible:ring-2 focus-visible:ring-blue-500 focus-visible:ring-offset-2"
                      onClick={authorizeWithStrava}>
                      let's go
                    </button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
        <Stats year={selectedYear} activities={activities} />
      </div>
    );
};

export default FetchDataFromStrava;
