// App.js
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import RunningStats from './components/RunningStats';
import axios from 'axios'; // Import axios for making HTTP requests

const App = () => {
  const [runningData, setRunningData] = useState([]);
  const [accessToken, setAccessToken] = useState(''); // State to store access token

  useEffect(() => {
    // Function to fetch Strava data
    const fetchStravaData = async () => {
      try {
        // Use your own Strava API endpoint and access token
        const response = await axios.get('https://www.strava.com/api/v3/athlete/activities', {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });

        // Update state with fetched running data
        setRunningData(response.data);
      } catch (error) {
        console.error('Error fetching Strava data:', error);
      }
    };

    // Call the fetchStravaData function
    fetchStravaData();
  }, [accessToken]); // Trigger the effect when the access token changes

  return (
    <div className="App">
      <Header />
      <RunningStats data={runningData} />
    </div>
  );
};

export default App;
