import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';

const App = () => {
  const [accessToken, setAccessToken] = useState('');
  const [runningData, setRunningData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStravaData = async () => {
      console.log("fetching strava data..."); 
      try {
        const response = await axios.get('https://www.strava.com/api/v3/athlete/activities', {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });
        setRunningData(response.data);
      } catch (error) {
        console.error('Error fetching Strava data:', error);
        navigate('/error');
      }
    };

    // Call the fetchStravaData function when the access token changes
    if (accessToken) {
      fetchStravaData();
    }
  }, [accessToken, navigate]);

  const handleLogin = () => {
    const stravaAuthUrl = `https://www.strava.com/oauth/authorize?client_id=120096&redirect_uri=http://localhost:3000/auth&response_type=code&scope=read,activity:read_all`;
    window.location.href = stravaAuthUrl;
  };

  return (
    <div className="App">
      <h1>on track</h1>
      {accessToken ? (
        <div>
          <p>Authenticated!</p>
          {/* Display running stats or other content */}
          <ul>
            {runningData.map((activity) => (
              <li key={activity.id}>
                <p>Date: {activity.start_date_local}</p>
                <p>Distance: {activity.distance} meters</p>
                {/* Add more details as needed */}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          <p>Not authenticated. Please login to fetch Strava data.</p>
          <button onClick={handleLogin}>Login with Strava</button>
        </div>
      )}
    </div>
  );
};

export default App;
