import React from 'react';
import { motion } from 'framer-motion';

const Stats = ({ year, activities }) => {
  const isAllTime = year === 'all-time';

  const handleClick = () => {

  }

  return (
    <div className="min-h-screen" style={{ backgroundColor: 'rgb(255, 254, 247)', padding: '2.5rem' }}>
      <h1 className="text-4xl font-bold mb-8">
        {isAllTime
          ? (
          <motion.span
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, transition: { duration: 0.5 } }}
            transition={{ type: 'spring', stiffness: 200, damping: 20 }}
            className="inline-block"
          > {'all-time stats'}
          </motion.span>)
          : (
            <>
              how you're doing in{' '}
              <motion.span
                initial={{ opacity: 0, y: -50 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, transition: { duration: 0.5 } }}
                transition={{ type: 'spring', stiffness: 200, damping: 20 }}
                className="inline-block"
              >
                {year}
              </motion.span>
            </>
          )
        }
      </h1>

      <div>
        <div>
          <h2>total distance traveled</h2>
          <p>{activities.reduce((sum, activity) => sum + activity.distance, 0)} meters</p>
        </div>
      </div>
      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={handleClick}
        className="bg-lightblue text-white font-wotfard text-l py-2 px-4 rounded mt-4"
      >
        see my stats
      </motion.button>
    </div>
  );
};

export default Stats;
