import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../components/LoginValidation'; 
import Validation from '../components/LoginValidation'; 

const Login = () => {
    const [values, setValues] = useState({
        username: '',
        password: ''
    });
    const [errors, setErrors] = useState({}); 
    const handleInput = (event) => {
        setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
    }

    const handleSubmit = (event) => {
        event.preventDefault(); 
        setErrors(Validation(values)); 
    }

  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
      <div className='bg-white p-3 rounded w-25'>
        <form action="" onSubmit={handleSubmit}>
          <div className='mb-3'>
            <label htmlFor='username' style={{ fontFamily: 'wotfard', fontSize: '20px', padding: '10px' }}>
              username
            </label>
            <input type='username' placeholder='enter username' name='username'
            style={{ 
                fontFamily: 'wotfard', 
                color: 'black', 
                border: '1px solid #ced4da', 
                borderRadius: '5px', 
                padding: '5px' }}
            onChange={handleInput} />
          </div>
          <div className='mb-3'>
            <label htmlFor='password' style={{ 
                fontFamily: 'wotfard', 
                fontSize: '20px', 
                padding: '10px' }}>
              password
            </label>
            <input type='password' placeholder='enter password' name='password'
            style={{ 
                fontFamily: 'wotfard', 
                color: 'black', 
                border: '1px solid #ced4da', 
                borderRadius: '5px', 
                padding: '5px' }} 
            onChange={handleInput} />
          </div>
          <button type='submit' className='btn btn-success' 
          style={{ 
            fontFamily: 'wotfard', 
            fontSize: '20px', 
            backgroundColor: 'rgb(217, 229, 238)', 
            padding: '5px' }}>log in</button>
          <p>don't already have an account?</p>
          <Link to='/signup' className='btn btn-default border' 
          style={{ 
            fontFamily: 'wotfard', 
            fontSize: '20px', 
            backgroundColor: 'rgb(217, 229, 238)', 
            padding: '5px' }}>create one</Link>
        </form>
      </div>
    </div>
  );
};

export default Login;
