import "./style.css";
import { useState } from "react";
import { Menu, MenuItem } from "./menu.jsx";

const menu = {
  closed: {
    scale: 0,
    transition: {
      delay: 0.15,
    },
  },

  open: {
    scale: 1,
    transition: {
      type: "spring",
      duration: 0.4,
      delayChildren: 0.2,
      staggerChildren: 0.05,
    },
  },
};

const item = {
  variants: {
    closed: { x: -16, opacity: 0 },
    open: { x: 0, opacity: 1 },
  },

  transition: { opacity: { duration: 0.2 } },
};

export default function Example() {
  const [open, setOpen] = useState(false);
  return (
    <Menu
      label="Options"
      open={open}
      setOpen={setOpen}
      animate={open ? "open" : "closed"}
      initial="closed"
      exit="closed"
      variants={menu}
    >
      <MenuItem {...item}>2020</MenuItem>
      <MenuItem {...item}>2021</MenuItem>
      <MenuItem {...item}>2022</MenuItem>
      <MenuItem {...item}>2023</MenuItem>
      <MenuItem {...item}>2024</MenuItem>
      <MenuItem {...item}>all-time</MenuItem>
    </Menu>
  );
}
