import React from 'react';
import { Bar } from 'react-chartjs-2';

const heartRateData = [
    { year: 2021, pace: '7:00', rate: 150 },
    // Add more data...
  ];

  // Function to group data by pace
  const groupByPace = (data) => {
    const groupedData = {};
    data.forEach(({ year, pace, rate }) => {
      if (!groupedData[year]) {
        groupedData[year] = {};
      }
      const paceKey = pace.replace(':', ''); // Remove ':' for simplicity
      groupedData[year][paceKey] = rate;
    });
    return groupedData;
  };

  const HeartRateChart = () => {
    const groupedData = groupByPace(heartRateData);

    // Extract years and paces for chart labels
    const years = Object.keys(groupedData);
    const paces = Object.keys(groupedData[years[0]]);

    // Create datasets for each pace
    const datasets = paces.map((pace) => ({
      label: `Pace ${pace.slice(0, 2)}:${pace.slice(2)} min/mi`,
      data: years.map((year) => groupedData[year][pace] || 0),
    }));

    const data = {
      labels: years.map((year) => year.toString()),
      datasets,
    };

    const options = {
      scales: {
        x: { title: { display: true, text: 'Year' } },
        y: { title: { display: true, text: 'Heart Rate' } },
      },
    };

    return <BarChart data={data} options={options} />;
  };

  export default HeartRateChart;
