import React from 'react';
import { motion } from 'framer-motion';

const Stats = ({year, activities }) => {

  const calculateAveragePace = (activities) => {
    if (activities.length === 0) {
      return 0; 
    }

    const totalPace = activities.reduce((sum, activity) => sum + activity.average_speed, 0);
    return Math.round(totalPace / activities.length);
  };


  const calculateAverageHeartRate = (activities, minPace, maxPace) => {
    const filteredActivities = activities.filter(activity => {
      const pace = activity.average_speed; // Assuming average_speed is in m/s

      return pace >= minPace && pace < maxPace;
    });

    if (filteredActivities.length === 0) {
      return 0; // Default value if no activities in the pace range
    }

    const totalHeartRate = filteredActivities.reduce((sum, activity) => sum + activity.average_heartrate, 0);
    return Math.round(totalHeartRate / filteredActivities.length);
  };

  return (
    <div className="min-h-screen" style={{backgroundColor: 'rgb(255, 254, 247)',
    padding: '2.5rem',}}>
      <h1 className="text-4xl font-bold mb-8">
        how you're doing in{' '}
        <motion.span
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, transition: { duration: 0.5 } }}
          transition={{ type: 'spring', stiffness: 200, damping: 20 }}
          className="inline-block"
        >
          {year}
        </motion.span>
      </h1>

      <div>
      <div>
        <h2>total distance</h2>
        <p>{activities.reduce((sum, activity) => sum + activity.distance, 0)} meters</p>
      </div>
      <div>
        <h2>average pace</h2>
        <p>{calculateAveragePace(activities)} m/s</p>
      </div>
      <div>
        <h2>average heart Rate</h2>
        <ul>
          <li>7:30 - 8:00: {calculateAverageHeartRate(activities, 7.5, 8)}</li>
          <li>8:00 - 8:30: {calculateAverageHeartRate(activities, 8, 8.5)}</li>
          <li>8:30 - 9:00: {calculateAverageHeartRate(activities, 8.5, 9)}</li>
          <li>9:30 - 10:00: {calculateAverageHeartRate(activities, 9.5, 10)}</li>
        </ul>
      </div>
    </div>
    </div>
  );
};

export default Stats;
