import React, { useState, useRef } from 'react';
import { motion, useScroll } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import ReactTyped from 'react-typed'; 
import DropdownMenu from './DropdownMenu';
import PaceHeartRateGraph from './HeartRateChart';
import HeartRateChart from './HeartRateChart';

const Stats = ({ activities }) => {
  const [selectedYear, setSelectedYear] = useState('all-time'); // Default to 'all-time'
  const isAllTime = selectedYear === 'all-time';

  const navigate = useNavigate();

  const handleClick = async () => {
    try {
      console.log("clicked");
      navigate('/');
      // navigate('/stats', { state: { selectedYear, stats: data } });
    } catch (error) {
      console.log(error);
      navigate('/error');
    }
  };

  const handleYearChange = (newDate) => {
    setSelectedYear(newDate);
  };

  const filteredActivities = isAllTime
    ? activities
    : activities.filter((activity) => new Date(activity.start_date).getFullYear() === parseInt(selectedYear));

  const totalDistance = filteredActivities.reduce((total, activity) => total + activity.distance, 0) * 0.000621371;

  const averagePace =
  filteredActivities.length > 0
    ? filteredActivities.reduce((total, activity) => total + activity.average_speed, 0) / filteredActivities.length
    : 0;

  const averagePaceInMinPerMile = averagePace !== 0 ? 1 / (averagePace * 0.037282) : 0;


  return (
    <div className="w-full min-h-screen" style={{ 
      backgroundColor: 'rgb(255, 254, 247)', 
      padding: '2.5rem', }}>
      <h1 className="text-4xl font-bold mb-8">
        {isAllTime ? (
          <motion.span
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, transition: { duration: 0.5 } }}
            transition={{ type: 'spring', stiffness: 200, damping: 20 }}
            className="inline-block"
          >
            {'all-time stats'}
          </motion.span>
        ) : (
          <>
            how you're doing in{' '}
            <motion.span
              key={selectedYear}
              initial={{ opacity: 0, y: -50 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, transition: { duration: 0.5 } }}
              transition={{ type: 'spring', stiffness: 200, damping: 20 }}
              className="inline-block"
            >
              {selectedYear}
            </motion.span>

          </>
        )}

      </h1>

      <div className='float-right'><DropdownMenu selectedYear={selectedYear} onYearChange={handleYearChange} /></div>

      <div className="flex flex-wrap justify-center">
        <section className='stat-box'>
          <h2>total distance traveled</h2>
          <p>{totalDistance.toFixed(2)} miles</p>
        </section>
        <section className='stat-box'>
          <h2>average pace</h2>
          <p>{averagePaceInMinPerMile.toFixed(2)} per mile</p>
        </section><section className='stat-box'>
          <h2>average heart rate</h2>
          <p>bpm</p>
        </section>

      </div>

      <div className='flex flex-wrap justify-center'>
      <p>how's heart rate training going?</p>
        <div>
          <HeartRateChart/>
        </div>
      </div>


      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={handleClick}
        className="bg-lightblue text-white font-wotfard text-l py-2 px-4 rounded mt-4"
      >
        back to home
      </motion.button>

      </div>

  );
};

export default Stats;
