import * as React from 'react';
import { BarChart } from '@mui/x-charts/BarChart';

const chartSetting = {
  xAxis: [
    {
      label: 'heart rate (bpm)',
    },
  ],
  width: 400,
  height: 400,
};
const dataset = [
  {
    pace: '7:00-7:30',
    heartrate: 150,
  }
];

const valueFormatter = (value) => `${value} bpm`;

const HeartRateChart = ({ activities }) => {
  const paceCategories = [
    { label: '7:00-7:30', minPace: 420, maxPace: 450 },
    { label: '7:30-8:00', minPace: 450, maxPace: 480 },
    { label: '8:00-8:30', minPace: 480, maxPace: 510 },
    { label: '8:30-9:00', minPace: 510, maxPace: 540 },
    { label: '9:00-9:30', minPace: 540, maxPace: 570 },
    { label: '9:30-10:00', minPace: 570, maxPace: 600 },
  ];

  // Function to filter activities into pace categories and calculate average heart rate
  const getAverageHeartRateByPace = (activities, paceCategory) => {
    const filteredActivities = activities.filter(activity => {
      const paceInSeconds = activity.pace_minutes * 60 + activity.pace_seconds;
      return paceInSeconds >= paceCategory.minPace && paceInSeconds < paceCategory.maxPace;
    });

    const totalHeartRate = filteredActivities.reduce((total, activity) => total + activity.heartrate, 0);
    const averageHeartRate = filteredActivities.length > 0 ? Math.round(totalHeartRate / filteredActivities.length) : 0;
    return averageHeartRate;
  };

  // Calculate average heart rate for each pace category
  const averageHeartRatesByPace = paceCategories.map(paceCategory => ({
    label: paceCategory.label,
    averageHeartRate: getAverageHeartRateByPace(activities, paceCategory),
  }));

  return (
    <div>
      <BarChart
      dataset={dataset}
      yAxis={[{ scaleType: 'band', dataKey: 'pace' }]}
      series={[{ dataKey: 'heartrate', valueFormatter, color: 'rgb(217, 229, 238)'}]}
      layout="horizontal"
      {...chartSetting}
    />
    </div>
  );
};

export default HeartRateChart;
