const StaggeredDropDown = ({ onSelectYear }) => {
  const [open, setOpen] = useState(false);
  const [selectedYear, setSelectedYear] = useState(null);

  const handleYearClick = (year) => {
    setSelectedYear(year);
    setOpen(false);
    onSelectYear(year); // Call the callback function with the selected year
  };

  return (
    <div className="p-8 pb-56 flex items-center justify-center bg-white">
      <motion.div animate={open ? "open" : "closed"} className="relative">
        <button
          onClick={() => setOpen((pv) => !pv)}
          className="flex items-center gap-2 px-3 py-2 rounded-md text-indigo-50 bg-indigo-500 hover:bg-indigo-500 transition-colors"
        >
          <span className="color-lightblue font-wotfard text-sm">view another year</span>
        </button>

        <motion.ul
          initial={wrapperVariants.closed}
          variants={wrapperVariants}
          style={{ originY: "top", translateX: "-50%" }}
          className="flex flex-col gap-2 p-2 rounded-lg bg-white shadow-xl absolute top-[120%] left-[50%] w-48 overflow-hidden"
        >
          <Option setOpen={setOpen} text="all-time stats" onClick={() => handleYearClick(null)} />
          <Option setOpen={setOpen} text="2020" onClick={() => handleYearClick(2020)} />
          <Option setOpen={setOpen} text="2021" onClick={() => handleYearClick(2021)} />
          <Option setOpen={setOpen} text="2022" onClick={() => handleYearClick(2022)} />
          <Option setOpen={setOpen} text="2023" onClick={() => handleYearClick(2023)} />
        </motion.ul>
      </motion.div>
    </div>
  );
};
