import { motion } from "framer-motion";
import { Dispatch, SetStateAction, useState } from "react";

  const StaggeredDropDown = () => {
    const [open, setOpen] = useState(false);
    const [selectedYear, setSelectedYear] = useState(null);

    return (
      <div className="p-8 pb-56 flex items-center justify-center bg-white">
        <motion.div animate={open ? "open" : "closed"} className="relative">
          <button
            onClick={() => setOpen((pv) => !pv)}
            className="flex items-center gap-2 px-3 py-2 rounded-md text-indigo-50 bg-indigo-500 hover:bg-indigo-500 transition-colors"
          >
            <span className="color-lightblue font-wotfard text-sm">view another year</span>

          </button>

          <motion.ul
            initial={wrapperVariants.closed}
            variants={wrapperVariants}
            style={{ originY: "top", translateX: "-50%" }}
            className="flex flex-col gap-2 p-2 rounded-lg bg-white shadow-xl absolute top-[120%] left-[50%] w-48 overflow-hidden"
          >
            <Option setOpen={setOpen} text="all-time stats" />
            <Option setOpen={setOpen} text="2020" />
            <Option setOpen={setOpen} text="2021" />
            <Option setOpen={setOpen} text="2022" />
            <Option setOpen={setOpen} text="2023" />
          </motion.ul>
        </motion.div>
      </div>
    );
  };

  const handleYearClick = ({text, setOpen}) => {
    onSelectYear(text);
    setOpen(false); // Close the dropdown after selecting a year
  };


  const Option = ({ text, setOpen }) => {
    return (
      <motion.li
        variants={itemVariants}
        onClick={() => handleSelection = (text)}
        className="flex items-center gap-2 w-full p-2 text-xs font-medium whitespace-nowrap rounded-md hover:bg-indigo-100 text-slate-700 hover:text-indigo-500 transition-colors cursor-pointer"
      >

        <span>{text}</span>
      </motion.li>
    );
  };

  export default StaggeredDropDown;

  const wrapperVariants = {
    open: {
      scaleY: 1,
      transition: {
        when: "beforeChildren",
        staggerChildren: 0.1,
      },
    },
    closed: {
      scaleY: 0,
      transition: {
        when: "afterChildren",
        staggerChildren: 0.1,
      },
    },
  };

  const iconVariants = {
    open: { rotate: 180 },
    closed: { rotate: 0 },
  };

  const itemVariants = {
    open: {
      opacity: 1,
      y: 0,
      transition: {
        when: "beforeChildren",
      },
    },
    closed: {
      opacity: 0,
      y: -15,
      transition: {
        when: "afterChildren",
      },
    },
  };

  const actionIconVariants = {
    open: { scale: 1, y: 0 },
    closed: { scale: 0, y: -7 },
  };
